import React from 'react';

const Statistics = ({ statistics }) => {
  // Calculate the active user percentage
  const activePercentage = (statistics.activeUsers / statistics.totalUsers) * 100;

  // Set gradient color based on the percentage
  const progressBarStyle = {
    width: `${activePercentage}%`,
    background: `linear-gradient(to right, #2ecc71 ${activePercentage}%, #e74c3c 0%)`, // Green to Red gradient
    boxShadow: `0 4px 6px rgba(0, 0, 0, 0.1)`, // Shadow effect
    borderRadius: '5px',
  };

  return (
    <div className="statistics">
      <h3>System Statistics</h3>
      <div>
        <p><strong>Total Users:</strong> {statistics.totalUsers}</p>
        <p><strong>Active Users:</strong> {statistics.activeUsers}</p>
        <p><strong>New Signups:</strong> {statistics.newSignups}</p>
      </div>

      {/* Progress Bar with color shadow */}
      <div className="progress-bar-container">
        <div className="progress-bar" style={progressBarStyle}></div>
      </div>
    </div>
  );
};

export default Statistics;
